	download = e => {
		window.open('https://sih2020backend.herokuapp.com/users/hallticket?token=' + localStorage.getItem('token'), '_blank');
	}


    render() {
        return (
            <MuiThemeProvider>
            <React.Fragment>

            <AppBar title="Download HallTicket" />
            <h3> Download your hall ticket by clicking the button below. </h3>
            <h3> Please note that your details will be verified using the QR code provided in the hall ticket at the time of the examination </h3>
			<RaisedButton
              label ="Back"
              primary = {false}
              variant="contained"
              style = {styles.button}
              onClick={this.back}
            />
            <RaisedButton
              label ="Download"
              primary = {true}
              style = {styles.button}
              variant="contained"
              onClick = {this.download}
            />

            </React.Fragment>
            </MuiThemeProvider>
        )
    }
}

const styles = {
	button:{
		margin : 15
	}
}

export default HallTicket
